/**
 * Interface used for window messages posted by the debugger.
 */
export interface IFluidClientDebuggerMessage<TData> {
    /**
     * Source of the message.
     *
     * @remarks
     *
     * Events generated by client-side debugger instances will always use `fluid-client-debugger`.
     *
     * Events generated by browser extensions, etc. in different contexts should use `fluid-client-debugger` as the prefix of this.
     * The tooling will ignore events missing this prefix.
     */
    source: string;

    /**
     * The type of event being sent.
     * Informs receivers of the {@link IFluidClientDebuggerMessage.data} format.
     */
    type: string;

    /**
     * Message data.
     */
    data: TData;
}
